import { Link } from 'react-router-dom'
import { useController } from '../../Controller'

import listReviewClasses from './listReviewClasses'

export default function ListReview({ review, user }) {
  const { deleteReview } = useController()

  const handleDelete = async () => {
    await deleteReview(review._id)
  }

  return (
    <div className={listReviewClasses.container}>
      <div className="flex 2xl:flex-row-reverse flex-col-reverse gap-6 items-center">
        <div className="flex flex-col w-5/6 gap-2">
          <p class="text-lg mb-4">{review.body}</p>
          <p class="text-gray-500 text-right italic self-end">
            {review.author}
          </p>
        </div>
        <Link to={review.moviePageLink}>
          <img
            src={review.moviePoster}
            alt="Movie poster"
            className="w-full xl:w-3/4 m-auto border border-slate-700 object-contain max-h-[400px]"
          />
        </Link>
        {user._id === review.user && (
          <div className="2xl:self-start self-end">
            <span
              className="cursor-pointer bg-gray-800 hover:bg-gray-900 rounded-full px-2 py-1"
              onClick={handleDelete}
            >
              &#10005;
            </span>
          </div>
        )}
      </div>
    </div>
  )
}
