import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'

import { useController } from '../Controller'
import ReviewForm from '../components/ReviewForm'
import reviewService from '../services/reviewService'

export function MoviePage() {
  const { getMoviePage, movie, setMovie } = useController()
  const params = useParams()
  const id = params.id

  const [review, setReviews] = useState([])
  async function createReview(review) {
    try {
      const newReview = await reviewService.createReview(review)
      setReviews((oldReviews) => [...oldReviews, newReview])
    } catch (error) {
      console.error(error)
    }
  }

  useEffect(() => {
    async function grabMovie() {
      await setMovie(getMoviePage(id))
    }
    grabMovie()
  }, [])

  console.log(movie)

  return (
    <div>
      <div>
        {movie && (
          <>
            <h1>{movie.Title}</h1>
            <h4>Rated {movie.Rated}</h4>
            <h4>{movie.Runtime}</h4>
            <h4>{movie.Released}</h4>

            <img src={movie.Poster} />
            <h4>{movie.Actors}</h4>
            <h4>Box Office: {movie.BoxOffice}</h4>
            <h4>{movie.Genre}</h4>
            <p>{movie.Plot}</p>
          </>
        )}
      </div>
      <div>
        <ReviewForm createReview={createReview} />
        {/* <Reviews /> */}
      </div>
    </div>
  )
}

