import { useController } from '../Controller'
import { Link } from 'react-router-dom'
import { Swiper, SwiperSlide } from 'swiper/react'

import 'swiper/css'

import 'swiper/css/effect-coverflow'
import 'swiper/css/pagination'
import 'swiper/css/navigation'

import { EffectCoverflow, Pagination, Navigation } from 'swiper'

export function MovieDisplay() {
  const { movies } = useController()
  const Loaded = ({ movie }) => {
    return (
      <Link to={`/${movie.title}`}>
        {/* <div className="text-xl font-bold flex flex-col items-center gap-8">
          <div className="p-2 bg-slate-800 rounded-lg px-6 py-8 ring-1 ring-slate-900/5 shadow-x">
            <img
              className="object-contain h-96 w-96"
              src={movie.poster}
              alt={movie.title}
            />
            <h2 className="">{movie.title}</h2>
          </div>
        </div> */}
        <div className="flex flex-col items-center">
          <img
            className="object-contain h-96 w-96"
            src={movie.poster}
            alt={movie.title}
          />
          <h2 className="">{movie.title}</h2>
        </div>
      </Link>
    )
  }

  const Loading = () => {
    return <></>
  }

  return (
    <div className="">
      <SwiperWrapper onSwiper={setSwiperReady}>
        {swiperReady && (
          <Swiper
            effect={'coverflow '}
            grabCursor={true}
            centeredSlides={true}
            loop={true}
            slidesPerView={'auto'}
            coverflowEffect={{
              rotate: 0,
              stretch: 0,
              depth: 100,
              modifier: 2.5,
            }}
            pagination={{ el: 'swiper-pagination', clickable: true }}
            navigation={{
              nextEl: '.swiper-button-next',
              prevEl: '.swiper-button-prev',
              clickable: true,
            }}
            modules={[EffectCoverflow, Pagination, Navigation]}
            className="swiper_container"
          >
            {movies.map((movie, index) => (
              <SwiperSlide key={index}>
                <div key={index}>
                  {movie ? <Loaded movie={movie} /> : <Loading />}
                </div>
              </SwiperSlide>
            ))}
            <div className="swiper-controller">
              <div className="swiper-button-prev"></div>
              <div className="swiper-button-next"></div>
              <div className="swiper-pagination"></div>
            </div>
          </Swiper>
        )}
      </SwiperWrapper>
    </div>
  )
}

function SwiperWrapper({ onSwiper, children }) {
  const [swiper, setSwiper] = useState(null)

  function handleSwiper(swiper) {
    setSwiper(swiper)
    onSwiper(true)
  }

  return <Swiper onSwiper={handleSwiper}>{children}</Swiper>
}
