import { useController } from '../Controller'
import { useState } from 'react'

export default function AdminPage({ user }) {
  const { createMovie, getAllMovies, movies } = useController()
  const [input, setInput] = useState('')
  const handleInputChange = (event) => {
    setInput(event.target.value)
  }

  const handleSubmit = async () => {
    await createMovie(input)
    setInput('')
  }

  useEffect(() => {
    async function fetchMovies() {
      await getAllMovies()
    }
    fetchMovies()
  }, [])

  return (
    <div className="bg-white rounded max-w-lg shadow-md p-8">
      <h1 className="text-3xl font-semibold mb-4">My To Do List:</h1>
      <p className="text-xs font-semibold mb-1">New Item</p>
      <input
        className="bg-indigo-50 rounded mb-4 w-80 h-10 outline-none p-4"
        type="text"
        value={input}
        onChange={handleInputChange}
        onKeyDown={(e) => {
          e.key === 'Enter' && handleSubmit()
        }}
      />
      <h4 className="text-md font-semibold">To Do Items:</h4>
      <ul className="list-disc">
        {movies.map((movie, i) => {
          if (movie.completed === false) return <Movie key={i} movie={movie} />
        })}
      </ul>
    </div>
  )
}
