import React, { useState } from 'react'

import { Route, Routes } from 'react-router-dom'
import { getUser } from '../services/usersService'
import { MoviePage } from '../pages/MoviePage'
import { LandingPage } from '../pages/LandingPage'
import { UserReviewPage } from '../pages/UserReviewsPage'
import { ProvideController } from '../Controller'
import AdminPage from '../pages/AdminPage'

import NavBar from '../components/NavBar/NavBar'
import Footer from '../components/Footer/Footer'

import AuthPage from '../pages/AuthPage'

import { appClasses } from '../appClasses'

function App() {
  const [user, setUser] = useState(getUser())
  return (
    <div className="App">
      {user && <NavBar user={user} />}

      <ProvideController>
        {user ? (
          <>
            <Routes>
              <Route path="/" element={<LandingPage />} />
              <Route
                path="/:id"
                element={<MoviePage user={user} setUser={setUser} />}
              />
              <Route path="/reviews" element={<UserReviewPage user={user} />} />
              {user.isAdmin && (
                <Route path="/admin/edit" element={<AdminPage user={user} />} />
              )}
            </Routes>
          </>
        ) : (
          <AuthPage setUser={setUser} />
        )}
      </ProvideController>

      {user && <Footer />}
    </div>
  )
}

export default App
