import { useState } from 'react'
import { useController } from '../../Controller'

import reviewClasses from './reviewClasses'

export default function Review({ review, user }) {
  const { deleteReview, updateReview } = useController()

  const [editing, setEditing] = useState(false)
  const [formData, setFormData] = useState({
    body: review.body,
    author: review.author,
  })
  const [error, setError] = useState('')

  function handleChange(evt) {
    setFormData({ ...formData, [evt.target.name]: evt.target.value })
  }

  const handleDelete = async () => {
    await deleteReview(review._id)
  }

  const handleSubmit = async (evt) => {
    evt.preventDefault()
    try {
      console.log(review)
      console.log(review.user)
      console.log(user._id)
      await updateReview(review._id, formData, user)
    } catch {
      setError('Editing Failed - Try Again')
    }
    setEditing(false)
  }

  return (
    <>
      <div className="flex flex-col justify-between border rounded border-slate-700 p-8 gap-4 mb-4 w-full sm:w-3/4 md:w-3/4 mx-auto">
        <div className="flex justify-between gap-4">
          {user._id === review.user && (
            <p
              className="cursor-pointer bg-gray-800 hover:bg-gray-900 rounded-full px-4 p-1"
              onClick={() => {
                setEditing(!editing)
              }}
            >
              Edit
            </p>
          )}
          {user._id === review.user && (
            <span
              className="cursor-pointer bg-gray-800 hover:bg-gray-900 rounded-full px-2 py-1"
              onClick={handleDelete}
            >
              &#10005;
            </span>
          )}
        </div>
        {editing ? (
          <form onSubmit={handleSubmit} className="flex flex-col mt-8 w-full">
            <div className="flex flex-col mb-4">
              <label className="font-semibold text-lg" htmlFor="body">
                Edit Review
              </label>
              <textarea
                className="border p-2 bg-slate-800 border-slate-700"
                rows="5"
                cols="100"
                name="body"
                placeholder={review.body}
                value={formData.body}
                onChange={handleChange}
              />
            </div>
            <div className="flex flex-col mb-4">
              <label className="font-semibold text-lg" htmlFor="author">
                Author
              </label>
              <input
                type="text"
                name="author"
                className="border p-2 bg-slate-800 border-slate-700"
                placeholder={review.author}
                value={formData.author}
                onChange={handleChange}
              />
            </div>
            <p>&nbsp;{error}</p>
            <button
              className="text-white font-semibold text-lg bg-gray-800 hover:bg-gray-900 focus:outline-none focus:ring-4 focus:ring-gray-300 rounded-lg px-5 py-2.5 mr-2 mb-2"
              type="submit"
            >
              Submit Changes
            </button>
          </form>
        ) : (
          <div className="flex flex-col">
            <p className="text-lg">{review.body}</p>
            <p className="self-end italic">{review.author}</p>
          </div>
        )}
      </div>
    </>
  )
}
