import { Link } from 'react-router-dom'
import { useController } from '../Controller'

export default function ListReview({ review, user }) {
  const { deleteReview } = useController()

  const handleDelete = async () => {
    await deleteReview(review._id)
  }

  return (
    <div className="border rounded border-slate-700 p-8 gap-4 mb-4 w-full sm:w-3/4 md:w-3/4 mx-auto">
      <div className="flex">
        <div className="flex flex-col w-5/6 pr-1">
          {user._id === review.user && (
            <span
              className="cursor-pointer bg-gray-800 hover:bg-gray-900 rounded-full px-2 py-1"
              onClick={handleDelete}
            >
              &#10005;
            </span>
          )}
          <p class="text-lg mb-4">{review.body}</p>
          <p class="text-gray-500 text-right">
            <i>{review.author}</i>
          </p>
        </div>
        <Link to={review.moviePageLink}>
          <div className="flex justify-end">
            <div className="w-4/6 border border-slate-700">
              <img
                src={review.moviePoster}
                alt="Movie poster"
                style={{
                  width: '100%',
                  height: 'auto',
                }}
              />
            </div>
          </div>
        </Link>
      </div>
    </div>
  )
}
