import { signUp } from '../services/usersService'
import { Component } from 'react'

export default class SignUpForm extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    confirm: '',
    error: '',
  }

  handleChange = (evt) => {
    this.setState({
      [evt.target.name]: evt.target.value,
      error: '',
    })
  }

  handleSubmit = async (evt) => {
    evt.preventDefault()
    console.log(evt)
    try {
      const formData = { ...this.state }
      delete formData.confirm
      delete formData.error
      // The promise returned by the signUp service method
      // will resolve to the user object included in the
      // payload of the JSON Web Token (JWT)
      const user = await signUp(formData)
      // Baby step
      this.props.setUser(user)
    } catch {
      // An error happened on the server
      this.setState({ error: 'Sign Up Failed - Try Again' })
    }
  }

  // We must override the render method
  // The render method is the equivalent to a function-based component
  // (its job is to return the UI)
  render() {
    const disable = this.state.password !== this.state.confirm

    // document.body.classList.add("signup-page");

    return (
      <div className="flex items-center justify-center w-full">
        <div className="w-1/2 h-screen bg-cover bg-center bg-[url('https://www.osvnews.com/wp-content/uploads/2023/02/Film-photo-via-Canva-Pro-600x337.png')]">
          1234
        </div>
        <div className="flex flex-col items-center justify-center min-h-screen w-1/2">
          <h2 className="text-3xl font-extrabold mb-16 text-center">
            Register to Sign Up
          </h2>
          <div className="border border-slate-700 bg-slate-800 rounded-md p-12 my-8 w-50 text-white shadow-2xl ">
            <form autoComplete="off" onSubmit={this.handleSubmit}>
              <div className="flex flex-col">
                <label className="w-full font-bold text-xl">Name</label>
                <input
                  type="text"
                  className="border p-2 mb-4 bg-slate-700 border-slate-600"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleChange}
                  required
                />
                <label className="w-full font-bold text-xl">Email</label>
                <input
                  type="email"
                  className="border p-2 mb-4 bg-slate-700 border-slate-600"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                  required
                />
                <label className="w-full font-bold text-xl">Password</label>
                <input
                  type="password"
                  className="border p-2 mb-4 bg-slate-700 border-slate-600"
                  name="password"
                  value={this.state.password}
                  onChange={this.handleChange}
                  required
                />
                <label className="w-full font-bold text-xl">Confirm</label>
                <input
                  type="password"
                  className="border p-2 mb-4 bg-slate-700 border-slate-600"
                  name="confirm"
                  value={this.state.confirm}
                  onChange={this.handleChange}
                  required
                />
                <button
                  type="submit"
                  className="bg-blue-500 text-white py-2 px-4 rounded-md shadow hover:bg-blue-600 transition-colors duration-300 mt-4"
                  disabled={disable}
                >
                  Sign Up
                </button>
              </div>
              <p className="text-red-500 text-sm mt-2">
                &nbsp;{this.state.error}
              </p>
            </form>
          </div>
          <h3
            className="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow w-32"
            onClick={() => this.props.setShowLogin(!this.props.showLogin)}
          >
            {this.props.showLogin ? 'SIGN UP' : 'LOG IN'}
          </h3>
        </div>
      </div>
    )
  }
}
