import { useController } from '../Controller'

export default function Review({ review }) {
  const { deleteReview } = useController()

  const handleDelete = async () => {
    await deleteReview(review._id)
  }

  return (
    <>
      <div className="flex items-center justify-between border rounded p-8">
        <div className="flex flex-col">
          <p>{review.body}</p>
          <p className="self-end">-{review.author}</p>
        </div>
        <div>
          <span className="cursor-pointer self-end" onClick={handleDelete}>
            &#10005;
          </span>
        </div>
      </div>
    </>
  )
}
