import { useController } from '../Controller'
import { useState, useEffect } from 'react'

export default function AdminPage({ user }) {
  const { getMovie, getAllMovies, movies } = useController()
  const [input, setInput] = useState('')
  const [isLoading, setIsLoading] = useState(true)

  const handleInputChange = (event) => {
    setInput(event.target.value)
  }

  const handleSubmit = async () => {
    setIsLoading(true)
    await getMovie(input)
    setInput('')
  }

  useEffect(() => {
    async function fetchTodos() {
      await getAllTodos()
      setIsLoading(false)
    }
    fetchTodos()
  }, [isLoading])

  return (
    <div>
      <h1 className="text-3xl font-semibold mb-4">Movie Selection</h1>
      <p className="text-xs font-semibold mb-1">New Movie</p>
      <input
        className="border p-2  bg-slate-800 border-slate-700"
        type="text"
        value={input}
        onChange={handleInputChange}
        onKeyDown={(e) => {
          e.key === 'Enter' && handleSubmit()
        }}
      />
      <h4 className="text-md font-semibold">Current movies:</h4>
      <ul className="list-disc">
        {movies.map((movie, i) => {
          {
            console.log(movie)
          }
        })}
      </ul>
    </div>
  )
}
