import { useController } from '../Controller'
import { useState, useEffect } from 'react'

export default function AdminPage({ user }) {
  const { getMovie, getAllMovies, movies } = useController()
  const [input, setInput] = useState('')
  const [isLoading, setIsLoading] = useState(true)

  const handleInputChange = (event) => {
    setInput(event.target.value)
  }

  const handleSubmit = async () => {
    setIsLoading(true)
    await getMovie(input)
    setInput('')
  }

  useEffect(() => {
    async function fetchTodos() {
      await getAllMovies()
      setIsLoading(false)
    }
    fetchTodos()
  }, [isLoading])

  return (
    <div>
      <h1 className="text-3xl font-semibold mb-4">Movie Selection</h1>
      <p className="text-xs font-semibold mb-1">New Movie</p>
      <input
        className="border p-2  bg-slate-800 border-slate-700"
        type="text"
        value={input}
        onChange={handleInputChange}
        onKeyDown={(e) => {
          e.key === 'Enter' && handleSubmit()
        }}
      />
      <h4 className="text-md font-semibold">Current movies:</h4>
      <div className="flex">
        {movies.map((movie, i) => {
          {
            ;<Link to={`/${movie.title}`}>
              ;
              <div className="group flex flex-col justify-center items-center p-2 h-full transition ease-in-out delay-150 group/item hover:-translate-y-4 hover:scale-110 duration-300">
                <img
                  className="object-contain w-full rounded-3xl group-hover:brightness-50"
                  src={movie.poster}
                  alt={movie.title}
                />
                <div className="opacity-0 group-hover:opacity-100 group-hover:brightness-100 duration-300 absolute inset-0 z-10 flex flex-col justify-end items-start  px-8 py-24">
                  <p className="text-2xl text-white font-semibold">
                    {movie.title}
                  </p>
                  <p>({movie.rating})</p>
                </div>
              </div>
            </Link>
          }
        })}
      </div>
    </div>
  )
}
