import { useState } from 'react'
import { useController } from '../Controller'
import { Link } from 'react-router-dom'
import Slider from 'react-slick'
import { FaArrowRight, FaArrowLeft } from 'react-icons/fa'

export function MovieDisplay() {
  const { movies } = useController()
  const [movieIndex, setMovieIndex] = useState(0)

  const NextArrow = ({ onClick }) => {
    return (
      <div className="arrow next" onClick={onClick}>
        <FaArrowRight />
      </div>
    )
  }

  const PrevArrow = ({ onClick }) => {
    return (
      <div className="arrow prev" onClick={onClick}>
        <FaArrowLeft />
      </div>
    )
  }

  const settings = {
    infinite: true, //to allow the slides to show infinitely
    lazyLoad: true,
    speed: 300, //this is the speed of slider in ms
    slidesToShow: 3, //number of slides to show up on screen
    centerMode: true,
    centerPadding: 0,
    nextArrow: <NextArrow />, //imported from 'react-icons'
    prevArrow: <PrevArrow />, //imported from 'react-icons'
  }

  const Loaded = ({ movie, index }) => {
    return (
      <Link to={`/${movie.title}`}>
        {/* <div className="text-xl font-bold flex flex-col items-center gap-8">
          <div className="p-2 bg-slate-800 rounded-lg px-6 py-8 ring-1 ring-slate-900/5 shadow-x">
            <img
              className="object-contain h-96 w-96 rounded-lg"
              src={movie.poster}
              alt={movie.title}
            />
            <h2 className="">{movie.title}</h2>
          </div>
        </div> */}
        <div className={index === movieIndex ? 'slide activeSlide' : 'slide'}>
          <img
            className="object-contain h-96 w-96 rounded-lg"
            src={movie.poster}
            alt={movie.title}
          />
        </div>
      </Link>
    )
  }

  const Loading = () => {
    return <></>
  }

  return (
    <div className="flex justify-evenly gap-8 items-center flex-wrap mr-64 ml-64 mb-64">
      <Slider {...settings}>
        {movies.map((movie, index) => (
          <div key={index}>
            {movie ? <Loaded movie={movie} index={index} /> : <Loading />}
          </div>
        ))}
      </Slider>
    </div>
  )
}
