import { useState } from 'react'
import { useController } from '../Controller'

export default function Review({ review }) {
  const { deleteReview, updateReview } = useController()

  const [editing, setEditing] = useState(false)
  const [formData, setFormData] = useState({
    title: '',
    url: '',
  })

  function handleChange(evt) {
    setFormData({ ...formData, [evt.target.name]: evt.target.value })
  }

  const handleDelete = async () => {
    await deleteReview(review._id)
  }

  const handleSubmit = async (evt) => {
    evt.preventDefault()
    try {
      await updateReview(review._id, formData)
    } catch {
      this.setState({ error: 'Editing Failed - Try Again' })
    }
    setEditing(false)
  }

  return (
    <>
      <div className="flex items-center justify-between border rounded p-8 gap-4 mb-4">
        <div className="flex flex-col">
          <p>{review.body}</p>
          <p className="self-end">- {review.author}</p>
        </div>
        <div className="flex gap-4">
          <p className="cursor-pointer">Edit</p>
          <span className="cursor-pointer" onClick={handleDelete}>
            &#10005;
          </span>
        </div>
      </div>
    </>
  )
}
