import { signUp } from '../../services/usersService'
import { Component } from 'react'

import authComponentsClasses from './authComponentsClasses'

export default class SignUpForm extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    confirm: '',
    error: '',
  }

  handleChange = (evt) => {
    this.setState({
      [evt.target.name]: evt.target.value,
      error: '',
    })
  }

  handleSubmit = async (evt) => {
    evt.preventDefault()
    console.log(evt)
    try {
      const formData = { ...this.state }
      delete formData.confirm
      delete formData.error
      const user = await signUp(formData)
      this.props.setUser(user)
    } catch {
      // An error happened on the server
      this.setState({ error: 'Sign Up Failed - Try Again' })
    }
  }

  render() {
    const disable = this.state.password !== this.state.confirm

    return (
      <div className={authComponentsClasses.container}>
        <div
          className={authComponentsClasses.image}
          style={{ backgroundImage: "url('/signup.jpg')" }}
        />
        <div className={authComponentsClasses.formContainer}>
          <h2 className={authComponentsClasses.heading}>Register to Sign Up</h2>

          <form
            autoComplete="off"
            className="border border-slate-700 bg-slate-800 rounded-md p-12 mb-8 w-50 text-white shadow-2xl "
            onSubmit={this.handleSubmit}
          >
            <div className="flex flex-col gap-2">
              <label className="font-semibold text-xl">Name</label>
              <input
                type="text"
                className="border p-2 mb-2 bg-slate-700 border-slate-600"
                name="name"
                placeholder="Enter your name"
                value={this.state.name}
                onChange={this.handleChange}
                required
              />
              <label className="font-semibold text-xl">Email</label>
              <input
                type="email"
                className="border p-2 mb-2 bg-slate-700 border-slate-600"
                name="email"
                placeholder="Enter your email"
                value={this.state.email}
                onChange={this.handleChange}
                required
              />
              <label className="font-semibold text-xl">Password</label>
              <input
                type="password"
                className="border p-2 mb-2 bg-slate-700 border-slate-600"
                name="password"
                placeholder="Create a password"
                value={this.state.password}
                onChange={this.handleChange}
                required
              />
              <label className="font-semibold text-xl">Confirm</label>
              <input
                type="password"
                className="border p-2 mb-2 bg-slate-700 border-slate-600"
                name="confirm"
                placeholder="Confirm your password"
                value={this.state.confirm}
                onChange={this.handleChange}
                required
              />
              <button
                type="submit"
                className="bg-blue-500 text-white py-2 px-4 rounded-md shadow hover:bg-blue-600 transition-colors duration-300 mt-4 font-semibold text-lg"
                disabled={disable}
              >
                Sign Up
              </button>
            </div>
            <p className="text-red-500 text-sm mt-2">
              &nbsp;{this.state.error}
            </p>
          </form>
          <div className="flex flex-col items-center gap-2">
            Already have an account?
            <h3
              className="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow w-32 text-center"
              onClick={() => this.props.setShowLogin(!this.props.showLogin)}
            >
              LOG IN
            </h3>
          </div>
        </div>
      </div>
    )
  }
}
