import { useController } from '../Controller'
import { Link } from 'react-router-dom'
import { Swiper, SwiperSlide } from 'swiper/react'

import 'swiper/css'
import 'swiper/css/navigation'

import { EffectCoverflow, Navigation } from 'swiper'

export function MovieDisplay() {
  const { movies } = useController()
  const Loaded = ({ movie }) => {
    return (
      <Link to={`/${movie.title}`}>
        {/* <div className="text-xl font-bold flex flex-col items-center gap-8">
          <div className="p-2 bg-slate-800 rounded-lg px-6 py-8 ring-1 ring-slate-900/5 shadow-x">
            <img
              className="object-contain h-96 w-96"
              src={movie.poster}
              alt={movie.title}
            />
            <h2 className="">{movie.title}</h2>
          </div>
        </div> */}
        <div className="flex flex-col items-center p-2 h-full bg-black">
          <img
            className="object-contain w-full"
            src={movie.poster}
            alt={movie.title}
          />
        </div>
        <h2 className="">{movie.title}</h2>
      </Link>
    )
  }

  const Loading = () => {
    return <></>
  }

  return (
    <div className="h-screen">
      <div>
        <p className="text-slate-400">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat. Duis aute irure dolor in
          reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
          pariatur.{' '}
        </p>
      </div>
      <h3 className="pl-8 mb-2 text-lg text-slate-400 font-medium">
        Our Picks
      </h3>
      <Swiper
        loop={true}
        slidesPerView={'auto'}
        navigation={{
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
          clickable: true,
        }}
        modules={[EffectCoverflow, Navigation]}
        className="swiper_container"
      >
        {movies.map((movie, index) => (
          <SwiperSlide key={index} style={{ width: '400px', height: '600px' }}>
            {movie ? <Loaded movie={movie} /> : <Loading />}
          </SwiperSlide>
        ))}
        <div className="swiper-controller">
          <div className="swiper-button-prev"></div>
          <div className="swiper-button-next"></div>
        </div>
      </Swiper>
    </div>
  )
}
