import { Link } from 'react-router-dom'
import { useController } from '../Controller'

export default function ListReview({ review, user }) {
  const { deleteReview } = useController()

  const handleDelete = async () => {
    await deleteReview(review._id)
  }

  return (
    <div className="border rounded border-slate-700 p-8 gap-4 mb-4 w-full lg:w-3/4 mx-auto">
      <div className="flex gap-6">
        <div className="flex flex-col w-5/6 pr-1 gap-2">
          {user._id === review.user && (
            <div>
              <span
                className="cursor-pointer bg-gray-800 hover:bg-gray-900 rounded-full px-2 py-1"
                onClick={handleDelete}
              >
                &#10005;
              </span>
            </div>
          )}
          <p class="text-lg mb-4">{review.body}</p>
          <p class="text-gray-500 text-right italic">{review.author}</p>
        </div>
        <Link
          to={review.moviePageLink}
          className="w-34 xl:w-1/2 border border-slate-700 self-center"
        >
          <img src={review.moviePoster} alt="Movie poster" className="w-full" />
        </Link>
      </div>
    </div>
  )
}
