import { useController } from '../Controller'
import { Link } from 'react-router-dom'

export function MovieDisplay() {
  const { movies } = useController()
  const Loaded = ({ movie }) => {
    return (
      <Link to={`/${movie.title}`}>
        <div className="text-xl font-bold flex flex-col items-center gap-8">
          <div className="flex flex-col items-center p-2 bg-slate-800 rounded-lg px-6 py-8 ring-1 ring-slate-900/5 shadow-x">
            <img
              className="object-contain h-96 w-96 rounded-lg"
              src={movie.poster}
              alt={movie.title}
            />
            <div className="flex">
              <h2 className="font-medium">{movie.title}</h2>
              <h2>({movie.rating})</h2>
            </div>
          </div>
        </div>
      </Link>
    )
  }

  const Loading = () => {
    return <></>
  }

  return (
    <div className="flex justify-evenly gap-8 items-center flex-wrap mr-64 ml-64 mb-64">
      {movies.map((movie, index) => (
        <div key={index}>{movie ? <Loaded movie={movie} /> : <Loading />}</div>
      ))}
    </div>
  )
}
